// util

function delay(f, ms = 300) {
  window.setTimeout(f, ms)
}

function addDefaultMutations(actions, mutations) {
  for (let k in mutations) {
    if (!actions.hasOwnProperty(k)) {
      actions[k] = ({ commit }, s) => {
        commit(k, s)
      }
    }
  }
}

module.exports = {
  delay,
  addDefaultMutations,
}
